mapper.update:
update 
	addresses
set 
	addr_name = #{name}
	, receiver_name = #{receiverName}
	, receiver_tel = #{receiverTel}
	, addr_basic = #{addrBasic}
	, addr_detail = #{addrDetail}
	, addr_zipcode = #{zipCode}
	, is_default_address = #{isDefaultAddress}
	, addr_updated_at = sysdate
where addr_no = #{no}
