mapper.select:
select count(*)
from orders

	where album_no = #{albumNo}

mapper.insert:

	select SEQ_ORDER_NO.nextval from dual


insert into ORDERS
(order_no
, order_price
, order_delivery_fee
, order_payment_method
, order_status
, addr_no
, sale_no
, album_no
, user_no)
values
(#{no}
, #{price}
, #{deliveryFee}
, #{paymentMethod}
, #{status}
, #{address.no}
, #{sale.no}  
, #{album.no}
, #{user.no})
